import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { motion } from 'framer-motion';
import './TraficLight.css'

const TraficLight = (props) => {
    const typeOBJ = useSelector(state => state.StatusBarState.toggleTraficType);
    const colorSelected = useSelector(state => state.StatusBarState.colorNowSelected);


    const brightness = useSelector(state => state.HeaderState.brightness);
    const delay = useSelector(state => state.HeaderState.countblink);
    const duration = useSelector(state => state.HeaderState.TIME);

    let tlContoroller = props.tlContoroller;
    let type = "vertical";

    const id_red = "color_red";
    const id_yelow = "color_yelow";
    const id_green = "color_green";

    let Rid_red = "color_red";
    let Rid_yelow = "color_yelow";
    let Rid_green = "color_green";

    const id_active = "_Active";


    switch (colorSelected) {
        case "red":
            Rid_red = id_red + id_active;
            break;
        case "yelow":
            Rid_yelow = id_yelow + id_active;
            break;

        case "green":
            Rid_green = id_green + id_active;
            break;
    }


    switch (typeOBJ) {
        case true:
            type = "vertical";
            break;
        case false:
            type = "horizontal";
            break;

    }


    const hlClick = (id_color) => {
        console.log(id_color);
       switch(id_color){
        case 0:
            Rid_red = Rid_red + ' AnimationClickTrafic';
            Rid_yelow = Rid_yelow + '';
            Rid_green = Rid_green + '';
            break;
        case 1:
            Rid_red = Rid_red + ' colors';
            Rid_yelow = Rid_yelow + ' colors AnimationClickTrafic';
            Rid_green = Rid_green + ' colors';
            break;
        case 2:
            Rid_red = Rid_red + ' colors';
            Rid_yelow = Rid_yelow + ' colors';
            Rid_green = Rid_green + ' colors AnimationClickTrafic';
            break;
       }
    }



    return (
        <div className='container_t'>
            <p> </p>
            <p> </p>
            <div className="contain_trafic" style={{'--testColor-background-color': 'rgb(196,0,0)'}}>
                <div className={"trafic trafic_" + type}>
                    <div onClick={() => hlClick(0)} className={'colors ' + Rid_red}></div>
                    <div onClick={() => hlClick(1)} className={'colors ' +Rid_yelow} ></div>
                    <div onClick={() => hlClick(2)} className={'colors ' +Rid_green} ></div>
                </div>
                {tlContoroller != null ? <div className='Controller_conteiner'>{tlContoroller}</div> : <p>Contoroller none active:</p>}
            </div>

        </div>
    );
}

export default TraficLight;
