import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { motion } from 'framer-motion';
import './TraficLight.css'

const TraficLight = (props) => {
    const typeOBJ = useSelector(state => state.StatusBarState.toggleTraficType);
    const colorSelected = useSelector(state => state.StatusBarState.colorNowSelected);
    const [isAnimating, setIsAnimating] = useState([false, false, false]);

    const brightness = useSelector(state => state.HeaderState.brightness);
    const delay = useSelector(state => state.HeaderState.countblink);
    const duration = useSelector(state => state.HeaderState.TIME);

    let tlContoroller = props.tlContoroller;
    let type = "vertical";

    const id_red = "color_red";
    const id_yelow = "color_yelow";
    const id_green = "color_green";

    let Rid_red = "color_red";
    let Rid_yelow = "color_yelow";
    let Rid_green = "color_green";

    const id_active = "_Active";



    const handleClick = (id) => {
        for(i = 0; i < 3; i++){
            if (i == id) setIsAnimating(!isAnimating[id]);
            else setIsAnimating(isAnimating[i] = fasle) ;
        }
        console.log(brightness / 100);
    };

    switch (colorSelected) {
        case "red":
            Rid_red = id_red + id_active;
            break;
        case "yelow":
            Rid_yelow = id_yelow + id_active;
            break;

        case "green":
            Rid_green = id_green + id_active;
            break;
    }


    switch (typeOBJ) {
        case true:
            type = "vertical";
            break;
        case false:
            type = "horizontal";
            break;

    }


    const hlClick = (color) => {
        console.log(color)
    }



    return (
        <div className='container'>
            <p> </p>
            <p> </p>
            <div className="contain_trafic">
                <div className={"trafic trafic_" + type}>


                    <motion.div
                        initial={{ opacity: 1 }}
                        animate={isAnimating ? { opacity: brightness/100 } : { opacity: 1 }}
                        transition={{ duration: 1 }}
                        whileTap={{ scale: 1.1, transition: { duration: 0.5, repeat: Infinity } }}
                        id={Rid_red} onClick={() => handleClick(0)} className='colors' >

                    </motion.div>
                    <motion.div
                        initial={{ opacity: 1 }}
                        animate={isAnimating ? { opacity: brightness/100 } : { opacity: 1 }}
                        transition={{ duration: 1 }}
                        whileTap={{ scale: 1.1, transition: { duration: 0.5, repeat: Infinity } }}
                        id={Rid_yelow} onClick={() => handleClick(1)} className='colors1' >

                    </motion.div>
                    <motion.div
                        initial={{ opacity: 1 }}
                        animate={isAnimating ? { opacity: brightness/100 } : { opacity: 1 }}
                        transition={{ duration: 1 }}
                        whileTap={{ scale: 1.1, transition: { duration: 0.5, repeat: Infinity } }}
                        id={Rid_green} onClick={() => handleClick(2)} className='colors2' >

                    </motion.div>

                </div>
                {tlContoroller != null ? <div className='Controller_conteiner'>{tlContoroller}</div> : <p>Contoroller none active:</p>}
            </div>

        </div>
    );
}

export default TraficLight;
