import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './TraficLight.css'

const TraficLight = (props) => {
    const typeOBJ = useSelector(state => state.StatusBarState.toggleTraficType);
    const colorSelected = useSelector(state => state.StatusBarState.colorNowSelected);

    const brightness = useSelector(state => state.HeaderState.brightness);
    const delay = useSelector(state => state.HeaderState.countblink);
    const duration = useSelector(state => state.HeaderState.TIME);

    let tlContoroller = props.tlContoroller;
    let type = "vertical";

    const id_red = "color_red";
    const id_yelow = "color_yelow";
    const id_green = "color_green";

    let Rid_red = "color_red";
    let Rid_yelow = "color_yelow";
    let Rid_green = "color_green";

    const id_active = "_Active";

    let color_red = [196,0,0]
    let color_green = [0,196,0]
    let color_yelow = [196,196,0]


    switch(colorSelected){
        case "red":
            Rid_red = id_red + id_active;
            break;
        case "yelow":
            Rid_yelow = id_yelow + id_active;
            break;

        case "green":
            Rid_green = id_green + id_active;
            break;
    }


    switch(typeOBJ){
        case true:
            type = "vertical";
            break;
        case false:
            type = "horizontal";
            break;

    }

    let animateClass;
    let click = false; 

    const hlClick = () => {
        click = !click;
        let intencity = (brightness / 255) * 100;
        animateClass = {
            transition: "background-color " + delay + "s ease",
            backgroundColor: null
        }

        switch(colorSelected){
            case "red":
                Rid_red = id_red + id_active;
                animateClass.backgroundColor = "rgb( " + intencity + ", 0 ,0)"
                break;
            case "yelow":
                Rid_yelow = id_yelow + id_active;
                animateClass.backgroundColor = "rgb( " + intencity + "," + intencity +" ,0)"
                break;

            case "green":
                Rid_green = id_green + id_active;
                animateClass.backgroundColor = "rgb(0, 0 ," + intencity +")"
                break;
        }
        console.log(animateClass);
    }

    useEffect(() => {
        let intencity = (brightness / 255) * 100;
        animateClass = {
            transition: "background-color " + delay + "s ease",
            backgroundColor: null
        }

        switch(colorSelected){
            case "red":
                Rid_red = id_red + id_active;
                animateClass.backgroundColor = "rgb( " + intencity + ", 0 ,0)"
                break;
            case "yelow":
                Rid_yelow = id_yelow + id_active;
                animateClass.backgroundColor = "rgb( " + intencity + "," + intencity +" ,0)"
                break;

            case "green":
                Rid_green = id_green + id_active;
                animateClass.backgroundColor = "rgb(0, 0 ," + intencity +")"
                break;
        }
    },[brightness, delay, duration, colorSelected])

    return (
        <div className='container'>
            <p> </p>
            <p> </p>
            <div className="contain_trafic">
                <div className={"trafic trafic_" + type}>
                    <div id={Rid_red} onClick={() => hlClick()} className='colors' style={animateClass}></div>
                    <div id={Rid_yelow} onClick={() => hlClick()} style={animateClass} className='colors'></div>
                    <div id={Rid_green} onClick={() => hlClick()} style={animateClass} className='colors'></div>
                </div>
                {tlContoroller != null ? <div className='Controller_conteiner'>{tlContoroller}</div> : <p>Contoroller none active:</p>}
            </div>

        </div>
    );
}

export default TraficLight;
